import java.util.*;
import java.util.stream.*;

record Service(String name, List<String> tags) {}

public class Main {
    public static void main(String[] args) {
        List<Service> services = List.of(
            new Service("auth", List.of("critical", "customer-facing")),
            new Service("billing", List.of("customer-facing")),
            new Service("docs", List.of("internal"))
        );

        Map<String, List<String>> criticalByAudience = services.stream()
            .collect(Collectors.groupingBy(
                service -> service.tags().contains("customer-facing") ? "customer-facing" : "internal",
                Collectors.filtering(
                    service -> service.tags().contains("critical"),
                    Collectors.mapping(Service::name, Collectors.toList())
                )
            ));

        for (String audience : List.of("customer-facing", "internal")) {
            List<String> names = criticalByAudience.getOrDefault(audience, List.of());
            System.out.println(audience + " -> " + names);
        }
    }
}

