import java.time.DayOfWeek;
import java.time.LocalDate;
import java.time.temporal.TemporalAdjusters;

public class Main {
    public static void main(String[] args) {
        LocalDate kickoff = LocalDate.of(2024, 4, 3);

        LocalDate sprintStart = kickoff.with(TemporalAdjusters.previousOrSame(DayOfWeek.MONDAY));
        LocalDate retro = sprintStart.plusWeeks(2).with(TemporalAdjusters.next(DayOfWeek.FRIDAY));
        LocalDate release = kickoff.with(TemporalAdjusters.lastDayOfMonth());

        System.out.println("Sprint start: " + sprintStart);
        System.out.println("Retro: " + retro);
        System.out.println("Release: " + release);
    }
}

