import java.nio.file.Files;
import java.nio.file.Path;

public class Main {
    public static void main(String[] args) throws Exception {
        Path dir = Files.createTempDirectory("artifacts");
        Path buildA = dir.resolve("build-A.txt");
        Path buildB = dir.resolve("build-B.txt");
        Files.writeString(buildA, "version=1.2.3\nstatus=ready\n");
        Files.writeString(buildB, "version=1.2.4\nstatus=ready\n");

        try {
            long position = Files.mismatch(buildA, buildB);
            if (position == -1L) {
                System.out.println("Files match");
            } else {
                System.out.println("Mismatch at byte " + position);
            }
        } finally {
            Files.deleteIfExists(buildA);
            Files.deleteIfExists(buildB);
            Files.deleteIfExists(dir);
        }
    }
}

