import java.io.IOException;
import java.nio.charset.StandardCharsets;
import java.nio.file.Files;
import java.nio.file.Path;
import java.nio.file.Paths;
import java.util.Comparator;
import java.util.List;
import java.util.stream.Collectors;
import java.util.zip.ZipEntry;
import java.util.zip.ZipFile;
import java.util.zip.ZipOutputStream;

public class Main {
    public static void main(String[] args) throws Exception {
        Path runRoot = Paths.get(System.getenv("RUN_TMP_DIR") != null
                ? System.getenv("RUN_TMP_DIR")
                : System.getProperty("java.io.tmpdir"));
        Path siteDir = runRoot.resolve("static-site");
        Path zipPath = runRoot.resolve("static-site.zip");

        if (Files.exists(siteDir)) {
            Files.walk(siteDir)
                    .sorted(Comparator.reverseOrder())
                    .forEach(path -> {
                        try {
                            Files.delete(path);
                        } catch (IOException ex) {
                            throw new RuntimeException(ex);
                        }
                    });
        }
        Files.deleteIfExists(zipPath);

        Files.createDirectories(siteDir.resolve("assets"));
        Files.writeString(siteDir.resolve("index.html"), "<h1>Launch</h1>\n", StandardCharsets.UTF_8);
        Files.writeString(siteDir.resolve("assets").resolve("style.css"), "body { font-family: sans-serif; }\n", StandardCharsets.UTF_8);

        try (ZipOutputStream zip = new ZipOutputStream(Files.newOutputStream(zipPath))) {
            Files.walk(siteDir)
                    .filter(Files::isRegularFile)
                    .forEach(path -> {
                        Path relative = siteDir.relativize(path);
                        ZipEntry entry = new ZipEntry(relative.toString().replace('\\', '/'));
                        try {
                            zip.putNextEntry(entry);
                            zip.write(Files.readAllBytes(path));
                            zip.closeEntry();
                        } catch (IOException ex) {
                            throw new RuntimeException(ex);
                        }
                    });
        }

        System.out.println("Created static-site.zip");
        try (ZipFile zipFile = new ZipFile(zipPath.toFile())) {
            List<String> names = zipFile.stream()
                    .map(ZipEntry::getName)
                    .filter(name -> !name.endsWith("/"))
                    .sorted()
                    .collect(Collectors.toList());
            System.out.println("Entries: " + String.join(", ", names));
        }
    }
}

