public class Main {
    sealed interface Payload permits TextPayload, NumberPayload, FlagPayload {}
    record TextPayload(String text) {}
    record NumberPayload(int value) {}
    record FlagPayload(boolean enabled) {}

    static String format(Payload payload) {
        return switch (payload) {
            case TextPayload(var text) -> text.toUpperCase();
            case NumberPayload(int value) when value > 99 -> "triple-digit";
            case NumberPayload(int value) -> "num:" + value;
            case FlagPayload(boolean enabled) -> enabled ? "enabled" : "disabled";
        };
    }

    public static void main(String[] args) {
        System.out.println(format(new TextPayload("hello")));
        System.out.println(format(new NumberPayload(150)));
        System.out.println(format(new NumberPayload(7)));
        System.out.println(format(new FlagPayload(true)));
    }
}

