from functools import lru_cache

# Cache results for each n to avoid exponential recursion.
@lru_cache(None)
def fib(n: int) -> int:
    # Base cases: 0 -> 0, 1 -> 1
    if n < 2:
        return n
    # Recurrence: F(n) = F(n-1) + F(n-2)
    return fib(n-1) + fib(n-2)

print(fib(10))  # 55

