from collections import OrderedDict

class LRU(OrderedDict):
    def __init__(self, cap):
        super().__init__()
        self.cap = cap
    def __setitem__(self, k, v):
        if k in self: del self[k]
        super().__setitem__(k, v)
        if len(self) > self.cap:
            self.popitem(last=False)

