import unittest
import io


def is_strong_password(text: str) -> bool:
    return (
        len(text) >= 8
        and any(ch.islower() for ch in text)
        and any(ch.isupper() for ch in text)
        and any(ch.isdigit() for ch in text)
    )


STRONG_CASES = []


class PasswordStrengthTests(unittest.TestCase):
    def test_password_matrix(self):
        cases = {
            "Password1": True,
            "weak": False,
            "Aa123456": True,
            "NoDigits!!": False,
        }
        for password, expected in cases.items():
            with self.subTest(password=password):
                result = is_strong_password(password)
                if expected and result:
                    STRONG_CASES.append(password)
                self.assertEqual(result, expected)


suite = unittest.defaultTestLoader.loadTestsFromTestCase(PasswordStrengthTests)
result = unittest.TextTestRunner(stream=io.StringIO()).run(suite)
print(
    f"Tests run: {result.testsRun}, failures: {len(result.failures)}, passed: {sorted(STRONG_CASES)}"
)

