from abc import ABC, abstractmethod

class Expression(ABC):
    @abstractmethod
    def interpret(self) -> int:
        pass

class NumberExpression(Expression):
    def __init__(self, number: int):
        self.number = number

    def interpret(self) -> int:
        return self.number

class AddExpression(Expression):
    def __init__(self, left: Expression, right: Expression):
        self.left = left
        self.right = right

    def interpret(self) -> int:
        return self.left.interpret() + self.right.interpret()

class SubtractExpression(Expression):
    def __init__(self, left: Expression, right: Expression):
        self.left = left
        self.right = right

    def interpret(self) -> int:
        return self.left.interpret() - self.right.interpret()

# Build and interpret: 5 + 3 - 2
five = NumberExpression(5)
three = NumberExpression(3)
two = NumberExpression(2)

addition = AddExpression(five, three)
subtraction = SubtractExpression(addition, two)

print(f"Result: {subtraction.interpret()}")

# More complex: (10 + 5) - (3 + 2)
left = AddExpression(NumberExpression(10), NumberExpression(5))
right = AddExpression(NumberExpression(3), NumberExpression(2))
result = SubtractExpression(left, right)

print(f"Complex Result: {result.interpret()}")

