from abc import ABC, abstractmethod
import math

class ShapeVisitor(ABC):
    @abstractmethod
    def visit_circle(self, circle: 'Circle') -> float:
        pass

    @abstractmethod
    def visit_rectangle(self, rectangle: 'Rectangle') -> float:
        pass

class Shape(ABC):
    @abstractmethod
    def accept(self, visitor: ShapeVisitor) -> float:
        pass

class Circle(Shape):
    def __init__(self, radius: float):
        self.radius = radius

    def accept(self, visitor: ShapeVisitor) -> float:
        return visitor.visit_circle(self)

class Rectangle(Shape):
    def __init__(self, width: float, height: float):
        self.width = width
        self.height = height

    def accept(self, visitor: ShapeVisitor) -> float:
        return visitor.visit_rectangle(self)

class AreaCalculator(ShapeVisitor):
    def visit_circle(self, circle: Circle) -> float:
        return math.pi * circle.radius ** 2

    def visit_rectangle(self, rectangle: Rectangle) -> float:
        return rectangle.width * rectangle.height

class PerimeterCalculator(ShapeVisitor):
    def visit_circle(self, circle: Circle) -> float:
        return 2 * math.pi * circle.radius

    def visit_rectangle(self, rectangle: Rectangle) -> float:
        return 2 * (rectangle.width + rectangle.height)

# Create shapes
circle = Circle(5)
rectangle = Rectangle(4, 6)

# Create visitors
area_calculator = AreaCalculator()
perimeter_calculator = PerimeterCalculator()

# Calculate areas and perimeters
print(f"Circle area: {circle.accept(area_calculator)}")
print(f"Circle perimeter: {circle.accept(perimeter_calculator)}")

print(f"Rectangle area: {rectangle.accept(area_calculator)}")
print(f"Rectangle perimeter: {rectangle.accept(perimeter_calculator)}")

