from abc import ABC, abstractmethod

class Device(ABC):
    @abstractmethod
    def is_enabled(self) -> bool:
        pass

    @abstractmethod
    def enable(self):
        pass

    @abstractmethod
    def disable(self):
        pass

    @abstractmethod
    def get_volume(self) -> int:
        pass

    @abstractmethod
    def set_volume(self, percent: int):
        pass

class TV(Device):
    def __init__(self):
        self._on = False
        self._volume = 30

    def is_enabled(self) -> bool:
        return self._on

    def enable(self):
        self._on = True
        print("TV is now ON")

    def disable(self):
        self._on = False
        print("TV is now OFF")

    def get_volume(self) -> int:
        return self._volume

    def set_volume(self, percent: int):
        self._volume = percent
        print(f"TV volume set to {self._volume}%")

class Radio(Device):
    def __init__(self):
        self._on = False
        self._volume = 50

    def is_enabled(self) -> bool:
        return self._on

    def enable(self):
        self._on = True
        print("Radio is now ON")

    def disable(self):
        self._on = False
        print("Radio is now OFF")

    def get_volume(self) -> int:
        return self._volume

    def set_volume(self, percent: int):
        self._volume = percent
        print(f"Radio volume set to {self._volume}%")

class RemoteControl(ABC):
    def __init__(self, device: Device):
        self.device = device

    def toggle_power(self):
        if self.device.is_enabled():
            self.device.disable()
        else:
            self.device.enable()

    def volume_up(self):
        self.device.set_volume(self.device.get_volume() + 10)

    def volume_down(self):
        self.device.set_volume(self.device.get_volume() - 10)

class BasicRemote(RemoteControl):
    pass

class AdvancedRemote(RemoteControl):
    def mute(self):
        self.device.set_volume(0)
        print("Device muted")

# Use the bridge pattern
tv = TV()
radio = Radio()

basic_remote = BasicRemote(tv)
