class User:
    def __init__(self, id: int, name: str):
        self.id = id
        self.name = name

class UnitOfWork:
    def __init__(self):
        self.new_objects = []
        self.dirty_objects = []
        self.deleted_objects = []

    def register_new(self, obj: User):
        self.new_objects.append(obj)
        print(f"Registered new: {obj.name}")

    def register_dirty(self, obj: User):
        self.dirty_objects.append(obj)
        print(f"Registered dirty: {obj.name}")

    def register_deleted(self, obj: User):
        self.deleted_objects.append(obj)

    def commit(self):
        print("\nCommitting transaction:")
        for u in self.new_objects:
            print(f"  INSERT {u.name}")
        for u in self.dirty_objects:
            print(f"  UPDATE {u.name}")
        for u in self.deleted_objects:
            print(f"  DELETE {u.name}")
        print("Transaction committed!")
        self.clear()

    def clear(self):
        self.new_objects.clear()
        self.dirty_objects.clear()
        self.deleted_objects.clear()

uow = UnitOfWork()
uow.register_new(User(1, "Alice"))
uow.register_new(User(2, "Bob"))
uow.register_dirty(User(1, "Alice Updated"))

uow.commit()

from abc import ABC, abstractmethod

class Specification(ABC):
    @abstractmethod
    def is_satisfied_by(self, item) -> bool:
        pass

    def and_spec(self, other):
        return AndSpecification(self, other)

class AndSpecification(Specification):
    def __init__(self, spec1, spec2):
        self.spec1 = spec1
        self.spec2 = spec2

    def is_satisfied_by(self, item) -> bool:
        return self.spec1.is_satisfied_by(item) and self.spec2.is_satisfied_by(item)

class User:
    def __init__(self, age: int, premium: bool):
        self.age = age
        self.premium = premium

class AgeSpec(Specification):
    def __init__(self, min_age: int):
        self.min_age = min_age

    def is_satisfied_by(self, user: User) -> bool:
        return user.age >= self.min_age

class PremiumSpec(Specification):
    def is_satisfied_by(self, user: User) -> bool:
        return user.premium

is_adult = AgeSpec(18)
is_premium = PremiumSpec()

user = User(25, True)
print(f"Is adult: {is_adult.is_satisfied_by(user)}")
print(f"Is premium adult: {is_adult.and_spec(is_premium).is_satisfied_by(user)}")

