            this.title = title;
            this.views = 0;
        }
    }

    static class StreamingService {
        Map<String, Video> videos = new HashMap<>();
        Map<String, Integer> viewCache = new HashMap<>(); // Simulates Redis

        void uploadVideo(String id, String title) {
            videos.put(id, new Video(id, title));
            System.out.println("Uploaded: " + title);
        }

        String streamVideo(String videoId, String resolution) {
            Video video = videos.get(videoId);
            if (video == null) return "Video not found";

            video.views++;
            viewCache.put(videoId, video.views);

            return String.format("Streaming '%s' at %s", video.title, resolution);
        }

        int getViews(String videoId) {
            return viewCache.getOrDefault(videoId, 0);
        }
    }

    public static void main(String[] args) {
        StreamingService youtube = new StreamingService();

        youtube.uploadVideo("v1", "Learn System Design");
        System.out.println(youtube.streamVideo("v1", "720p"));
        System.out.println(youtube.streamVideo("v1", "1080p"));
        System.out.println("Total views: " + youtube.getViews("v1"));
    }
}

