// Uber Ride Matching System
import java.util.*;

class Main {
    static class Driver {
        String id;
        double lat, lon;
        boolean available;

        Driver(String id, double lat, double lon) {
            this.id = id;
            this.lat = lat;
            this.lon = lon;
            this.available = true;
        }

        double distanceTo(double lat, double lon) {
            double dlat = Math.abs(this.lat - lat);
            double dlon = Math.abs(this.lon - lon);
            return Math.sqrt(dlat * dlat + dlon * dlon);
        }
    }

    static class RideService {
        List<Driver> drivers = new ArrayList<>();

        void addDriver(String id, double lat, double lon) {
            drivers.add(new Driver(id, lat, lon));
        }

        Driver findNearestDriver(double riderLat, double riderLon) {
            Driver nearest = null;
            double minDist = Double.MAX_VALUE;

            for (Driver d : drivers) {
                if (d.available) {
                    double dist = d.distanceTo(riderLat, riderLon);
                    if (dist < minDist) {
                        minDist = dist;
                        nearest = d;
                    }
                }
            }
            return nearest;
        }

        String requestRide(double lat, double lon) {
            Driver driver = findNearestDriver(lat, lon);
            if (driver == null) return "No drivers available";
            driver.available = false;
            return "Matched with driver: " + driver.id;
        }
    }

    public static void main(String[] args) {
        RideService uber = new RideService();
        uber.addDriver("D1", 37.7749, -122.4194);
        uber.addDriver("D2", 37.7849, -122.4094);

        System.out.println(uber.requestRide(37.7750, -122.4195));
        System.out.println(uber.requestRide(37.7850, -122.4090));
    }
}

