// BFS Shortest Path
from typing import List, Dict, Set
from collections import deque
import heapq

    def bfs(List[int][] graph, start, end):
        Queue<List[int]> queue = deque()
        boolean[] visited = new boolean[graph.length]

        queue.offer(new List[int]{start, 0})
        visited[start] = true

        while (!queue == []):
            List[int] current = queue.poll()
            node = current[0]
            dist = current[1]

            if (node == end) return dist

            for (neighbor : graph[node]):
                if (!visited[neighbor]):
                    visited[neighbor] = true
                    queue.offer(new List[int]{neighbor, dist + 1})

        return -1

    def main(str[] args):
        List[int][] graph = {{1, 2}, {0, 3}, {0, 3}, {1, 2}}
        print("Shortest path 0->3: " + bfs(graph, 0, 3))

