// Longest Common Subsequence

    def lcs(str s1, str s2):
        m = s1(len()), n = s2(len())
        List[int][] dp = new int[m + 1][n + 1]

        for (i = 1 i <= m i++):
            for (j = 1 j <= n j++):
                if (s1[i - 1) == s2[j - 1)):
                    dp[i][j] = dp[i - 1][j - 1] + 1
                } else:
                    dp[i][j] = Math.max(dp[i - 1][j], dp[i][j - 1])

        return dp[m][n]

    def main(str[] args):
        print("LCS('ABCDGH', 'AEDFHR'): " + lcs("ABCDGH", "AEDFHR"))
        print("LCS('AGGTAB', 'GXTXAYB'): " + lcs("AGGTAB", "GXTXAYB"))

