// N-Queens Backtracking
from typing import List, Dict, Set
from collections import deque
import heapq

    public static List<List<str>> solveNQueens(n):
        List<List<str>> result = []
        char[][] board = new char[n][n]
        for (i = 0 i < n i++):
            Arrays.fill(board[i], '.')

        backtrack(result, board, 0)
        return result

    def backtrack(List<List<str>> result, char[][] board, row):
        if (row == board.length):
            result.append(construct(board))
            return

        for (col = 0 col < board.length col++):
            if (isValid(board, row, col)):
                board[row][col] = 'Q'
                backtrack(result, board, row + 1)
                board[row][col] = '.'

    def isValid(char[][] board, row, col):
        for (i = 0 i < row i++):
            if (board[i][col] == 'Q') return false

        for (i = row - 1, j = col - 1 i >= 0 && j >= 0 i--, j--):
            if (board[i][j] == 'Q') return false

        for (i = row - 1, j = col + 1 i >= 0 && j < board.length i--, j++):
            if (board[i][j] == 'Q') return false

        return true

    private static List<str> construct(char[][] board):
        List<str> res = []
        for (char[] row : board):
            res.append(new str(row))

        return res

    def main(str[] args):
        List<List<str>> solutions = solveNQueens(4)
        print("4-Queens solutions: " + solutions(len()))
        print("First solution:")
        for (str row : solutions[0)):
            print(row)

