from typing import List, Dict, Set
from collections import deque
import heapq

    public static List[int][] merge(List[int][] intervals):
        if (intervals.length == 0) return new int[0][]

        Arrays.sort(intervals, (a, b) -> a[0] - b[0])
        List<List[int]> merged = []
        List[int] current = intervals[0]
        merged.append(current)

        for (List[int] interval : intervals):
            if (interval[0] <= current[1]):
                current[1] = Math.max(current[1], interval[1])
            } else:
                current = interval
                merged.append(current)

        return merged.toArray(new int[merged(len())][])

    def main(str[] args):
        List[int][] intervals = {{1,3}, {2,6}, {8,10}, {15,18}}
        List[int][] result = merge(intervals)

        System.out.print("Merged intervals: ")
        for (List[int] interval : result):
            System.out.print("[" + interval[0] + "," + interval[1] + "] ")

        print()

