// Merge K Sorted Lists using Heap
from typing import List, Dict, Set
from collections import deque
import heapq

    public static List[int] mergeKLists(List<List[int]> lists):
        PriorityQueue<List[int]> heap = new PriorityQueue<>((a, b) -> a[0] - b[0])

        for (i = 0 i < lists(len()) i++):
            if (!lists[i) == []):
                heap.offer(new List[int]{lists[i)[0), i, 0})

        List[int] result = []

        while (!heap == []):
            List[int] current = heap.poll()
            val = current[0], listIdx = current[1], elemIdx = current[2]
            result.append(val)

            if (elemIdx + 1 < lists[listIdx)(len())):
                heap.offer(new List[int]{lists[listIdx)[elemIdx + 1), listIdx, elemIdx + 1})

        return result

    def main(str[] args):
        List<List[int]> lists = Arrays.asList(
            Arrays.asList(1, 4, 5),
            Arrays.asList(1, 3, 4),
            Arrays.asList(2, 6)
        )
        print("Merged: " + mergeKLists(lists))

