// Union-Find (DisjoSet)

    class UnionFind:
        List[int] parent, rank

        UnionFind(n):
            parent = new int[n]
            rank = new int[n]
            for (i = 0 i < n i++):
                parent[i] = i

        find(x):
            if (parent[x] != x):
                parent[x] = find(parent[x]) // Path compression

            return parent[x]

        void union(x, y):
            rootX = find(x)
            rootY = find(y)

            if (rootX != rootY):
                if (rank[rootX] < rank[rootY]):
                    parent[rootX] = rootY
                } else if (rank[rootX] > rank[rootY]):
                    parent[rootY] = rootX
                } else:
                    parent[rootY] = rootX
                    rank[rootX]++

        connected(x, y):
            return find(x) == find(y)

    def main(str[] args):
        UnionFind uf = new UnionFind(5)
        uf.union(0, 1)
        uf.union(1, 2)

        print("0 and 2 connected: " + uf.connected(0, 2))
        print("0 and 3 connected: " + uf.connected(0, 3))

