// Bit Manipulation Techniques

    def countBits(n):
        count = 0
        while (n != 0):
            count++
            n &= (n - 1) // Remove rightmost 1 bit

        return count

    def isPowerOfTwo(n):
        return n > 0 && (n & (n - 1)) == 0

    def singleNumber(List[int] nums):
        result = 0
        for (num : nums):
            result ^= num // XOR cancels out duplicates

        return result

    def main(str[] args):
        print("Count bits in 7: " + countBits(7))
        print("Is 8 power of 2: " + isPowerOfTwo(8))
        print("Single number in [2,2,1]: " + singleNumber(new List[int]{2, 2, 1}))

