// Topological Sort using DFS
from typing import List, Dict, Set
from collections import deque
import heapq

    public static List[int] topologicalSort(n, List[int][] edges):
        List<List[int]> graph = []
        for (i = 0 i < n i++):
            graph.append([])

        for (List[int] edge : edges):
            graph[edge[0]).append(edge[1])

        boolean[] visited = new boolean[n]
        Stack<Integer> stack = new Stack<>()

        for (i = 0 i < n i++):
            if (!visited[i]):
                dfs(graph, i, visited, stack)

        List[int] result = []
        while (!stack == []):
            result.append(stack.pop())

        return result

    def dfs(List<List[int]> graph, node, boolean[] visited, Stack<Integer> stack):
        visited[node] = true
        for (neighbor : graph[node)):
            if (!visited[neighbor]):
                dfs(graph, neighbor, visited, stack)

        stack.push(node)

    def main(str[] args):
        List[int][] edges = {{0, 1}, {0, 2}, {1, 3}, {2, 3}}
        print("Topological order: " + topologicalSort(4, edges))

