// Complete Todo List REST API - Production Ready
import java.util.*;
import java.time.LocalDateTime;
import java.time.format.DateTimeFormatter;
import java.util.stream.Collectors;

// Model: Todo entity
class Todo {
    private final int id;
    private String title;
    private boolean completed;
    private final LocalDateTime createdAt;

    public Todo(int id, String title) {
        if (title == null || title.trim().isEmpty()) {
            throw new IllegalArgumentException("Title cannot be empty");
        }
        this.id = id;
        this.title = title.trim();
        this.completed = false;
        this.createdAt = LocalDateTime.now();
    }

    // Getters
    public int getId() { return id; }
    public String getTitle() { return title; }
    public boolean isCompleted() { return completed; }
    public LocalDateTime getCreatedAt() { return createdAt; }

    // Business methods
    public void setTitle(String title) {
        if (title == null || title.trim().isEmpty()) {
            throw new IllegalArgumentException("Title cannot be empty");
        }
        this.title = title.trim();
    }

    public void markComplete() { this.completed = true; }
    public void markIncomplete() { this.completed = false; }

    @Override
    public String toString() {
        DateTimeFormatter formatter = DateTimeFormatter.ofPattern("yyyy-MM-dd HH:mm");
        String status = completed ? "[X]" : "[ ]";
        return String.format("%s Todo #%d: %s (created: %s)",
            status, id, title, createdAt.format(formatter));
    }
}

// Service: Business logic layer
class TodoService {
    private final List<Todo> todos;
    private int nextId;

    public TodoService() {
        this.todos = new ArrayList<>();
        this.nextId = 1;
    }

    // CREATE
    public Todo create(String title) {
        Todo todo = new Todo(nextId++, title);
        todos.add(todo);
        return todo;
    }

    // READ
    public List<Todo> getAll() {
        return new ArrayList<>(todos); // Return copy for immutability
    }

    public Optional<Todo> getById(int id) {
        return todos.stream()
            .filter(t -> t.getId() == id)
            .findFirst();
    }

    public List<Todo> getCompleted() {
        return todos.stream()
            .filter(Todo::isCompleted)
            .collect(Collectors.toList());
    }

    public List<Todo> getPending() {
        return todos.stream()
            .filter(t -> !t.isCompleted())
            .collect(Collectors.toList());
    }

    public List<Todo> search(String keyword) {
        String lower = keyword.toLowerCase();
        return todos.stream()
            .filter(t -> t.getTitle().toLowerCase().contains(lower))
            .collect(Collectors.toList());
    }

    // UPDATE
    public boolean update(int id, String newTitle) {
        Optional<Todo> todo = getById(id);
        if (todo.isPresent()) {
            todo.get().setTitle(newTitle);
            return true;
        }
        return false;
    }

    public boolean toggleComplete(int id) {
        Optional<Todo> todo = getById(id);
        if (todo.isPresent()) {
            if (todo.get().isCompleted()) {
                todo.get().markIncomplete();
            } else {
                todo.get().markComplete();
            }
            return true;
        }
        return false;
    }

    // DELETE
    public boolean delete(int id) {
        return todos.removeIf(t -> t.getId() == id);
    }

    public void clear() {
        todos.clear();
    }

    // STATS
    public Map<String, Integer> getStats() {
        Map<String, Integer> stats = new HashMap<>();
        stats.put("total", todos.size());
        stats.put("completed", (int) todos.stream().filter(Todo::isCompleted).count());
        stats.put("pending", (int) todos.stream().filter(t -> !t.isCompleted()).count());
        return stats;
    }
}

// Main: Demo application
public class Main {
    public static void main(String[] args) {
        TodoService service = new TodoService();

        System.out.println("=== TODO LIST API DEMO ===\n");

        // CREATE todos
        System.out.println("Creating todos...");
        service.create("Learn Java fundamentals");
        service.create("Build portfolio projects");
        service.create("Practice coding interviews");
        service.create("Update resume and LinkedIn");
        service.create("Apply to 10 companies");

        // READ all
        System.out.println("\nAll Todos:");
        service.getAll().forEach(System.out::println);

        // UPDATE
        System.out.println("\nMarking todos 1 and 2 as complete...");
        service.toggleComplete(1);
        service.toggleComplete(2);

        // READ filtered
        System.out.println("\nCompleted Todos:");
        service.getCompleted().forEach(System.out::println);

        System.out.println("\nPending Todos:");
        service.getPending().forEach(System.out::println);

        // SEARCH
        System.out.println("\nSearching for 'portfolio'...");
        service.search("portfolio").forEach(System.out::println);

        // STATS
        System.out.println("\nStatistics:");
        Map<String, Integer> stats = service.getStats();
        System.out.println("  Total: " + stats.get("total"));
        System.out.println("  Completed: " + stats.get("completed"));
        System.out.println("  Pending: " + stats.get("pending"));

        // DELETE
        System.out.println("\nDeleting todo #5...");
        service.delete(5);

        System.out.println("\nFinal Todo List:");
