// Complete Blog Platform with Authentication
import java.util.*;
import java.time.LocalDateTime;
import java.security.MessageDigest;
import java.nio.charset.StandardCharsets;

// User Model
class User {
    private final int id;
    private final String username;
    private final String passwordHash;
    private final LocalDateTime createdAt;

    public User(int id, String username, String passwordHash) {
        this.id = id;
        this.username = username;
        this.passwordHash = passwordHash;
        this.createdAt = LocalDateTime.now();
    }

    public int getId() { return id; }
    public String getUsername() { return username; }
    public String getPasswordHash() { return passwordHash; }
    public LocalDateTime getCreatedAt() { return createdAt; }
}

// BlogPost Model
class BlogPost {
    private final int id;
    private String title;
    private String content;
    private final int authorId;
    private final LocalDateTime createdAt;
    private LocalDateTime updatedAt;

    public BlogPost(int id, String title, String content, int authorId) {
        this.id = id;
        this.title = title;
        this.content = content;
        this.authorId = authorId;
        this.createdAt = LocalDateTime.now();
        this.updatedAt = LocalDateTime.now();
    }

    public int getId() { return id; }
    public String getTitle() { return title; }
    public String getContent() { return content; }
    public int getAuthorId() { return authorId; }
    public LocalDateTime getCreatedAt() { return createdAt; }

    public void update(String title, String content) {
        this.title = title;
        this.content = content;
        this.updatedAt = LocalDateTime.now();
    }

    @Override
    public String toString() {
        return String.format("Post #%d: %s by User#%d", id, title, authorId);
    }
}

// Authentication Service
class AuthService {
    private final Map<String, User> users = new HashMap<>();
    private int nextUserId = 1;

    public Optional<User> register(String username, String password) {
        if (users.containsKey(username)) {
            return Optional.empty(); // Username exists
        }
        String hash = hashPassword(password);
        User user = new User(nextUserId++, username, hash);
        users.put(username, user);
        return Optional.of(user);
    }

    public Optional<User> login(String username, String password) {
        User user = users.get(username);
        if (user != null && user.getPasswordHash().equals(hashPassword(password))) {
            return Optional.of(user);
        }
        return Optional.empty();
    }

    private String hashPassword(String password) {
        try {
            MessageDigest md = MessageDigest.getInstance("SHA-256");
            byte[] hash = md.digest(password.getBytes(StandardCharsets.UTF_8));
            return Base64.getEncoder().encodeToString(hash);
        } catch (Exception e) {
            throw new RuntimeException("Hashing failed", e);
        }
    }
}

// Blog Service
class BlogService {
    private final List<BlogPost> posts = new ArrayList<>();
    private int nextPostId = 1;

    public BlogPost createPost(String title, String content, int authorId) {
        BlogPost post = new BlogPost(nextPostId++, title, content, authorId);
        posts.add(post);
        return post;
    }

    public List<BlogPost> getAllPosts() {
        return new ArrayList<>(posts);
    }

    public List<BlogPost> getPostsByAuthor(int authorId) {
        List<BlogPost> result = new ArrayList<>();
        for (BlogPost post : posts) {
            if (post.getAuthorId() == authorId) {
                result.add(post);
            }
        }
        return result;
    }

    public Optional<BlogPost> getPostById(int id) {
        for (BlogPost post : posts) {
            if (post.getId() == id) {
                return Optional.of(post);
            }
        }
        return Optional.empty();
    }

    public boolean updatePost(int postId, String title, String content, int userId) {
        Optional<BlogPost> post = getPostById(postId);
        if (post.isPresent() && post.get().getAuthorId() == userId) {
            post.get().update(title, content);
            return true;
        }
        return false; // Not found or not authorized
    }

    public boolean deletePost(int postId, int userId) {
        Optional<BlogPost> post = getPostById(postId);
        if (post.isPresent() && post.get().getAuthorId() == userId) {
            posts.remove(post.get());
            return true;
        }
        return false;
    }
}

public class Main {
    public static void main(String[] args) {
        AuthService auth = new AuthService();
        BlogService blog = new BlogService();

        System.out.println("=== BLOG PLATFORM DEMO ===\n");

        // Register users
        System.out.println("Registering users...");
        Optional<User> alice = auth.register("alice", "password123");
        Optional<User> bob = auth.register("bob", "secret456");

        // Login
        System.out.println("\nLogging in...");
        Optional<User> aliceLogin = auth.login("alice", "password123");
        if (aliceLogin.isPresent()) {
            System.out.println("Alice logged in successfully!");

            // Create posts
            blog.createPost("My First Post", "Hello World!", aliceLogin.get().getId());
            blog.createPost("Java Tips", "Use Optional for null safety", aliceLogin.get().getId());
        }

        Optional<User> bobLogin = auth.login("bob", "secret456");
        if (bobLogin.isPresent()) {
            System.out.println("Bob logged in successfully!");
            blog.createPost("Getting Started", "This is my blog", bobLogin.get().getId());
        }

        // List all posts
        System.out.println("\nAll Posts:");
        blog.getAllPosts().forEach(System.out::println);

        // Alice updates her post
        System.out.println("\nAlice updating post #1...");
        blog.updatePost(1, "My First Post (Updated)", "Hello World - Now with more content!", aliceLogin.get().getId());

        // Try unauthorized update (should fail)
        System.out.println("Bob trying to update Alice's post...");
        boolean unauthorized = blog.updatePost(1, "Hacked", "Malicious content", bobLogin.get().getId());
        System.out.println("Unauthorized update: " + (unauthorized ? "SUCCESS (BUG!)" : "BLOCKED (Correct)"));

        // Alice deletes her second post
        System.out.println("\nAlice deleting post #2...");
        blog.deletePost(2, aliceLogin.get().getId());

        System.out.println("\nFinal Posts:");
        blog.getAllPosts().forEach(System.out::println);

        System.out.println("\n=== PROJECT COMPLETE ===");
    }
}

