import java.util.*;

class Product {
    private final int id;
    private final String name;
    private final double price;
    private final String category;

    public Product(int id, String name, double price, String category) {
        this.id = id;
        this.name = name;
        this.price = price;
        this.category = category;
    }

    public int getId() { return id; }
    public String getName() { return name; }
    public double getPrice() { return price; }
    public String getCategory() { return category; }

    @Override
    public String toString() {
        return String.format("#%d %s - $%.2f (%s)", id, name, price, category);
    }
}

class CartItem {
    private final Product product;
    private int quantity;

    public CartItem(Product product, int quantity) {
        this.product = product;
        this.quantity = quantity;
    }

    public Product getProduct() { return product; }
    public int getQuantity() { return quantity; }
    public void setQuantity(int quantity) { this.quantity = quantity; }

    public double getSubtotal() {
        return product.getPrice() * quantity;
    }

    @Override
    public String toString() {
        return String.format("%s x%d = $%.2f", product.getName(), quantity, getSubtotal());
    }
}

class DiscountCode {
    private final String code;
    private final String type;
    private final double value;
    private final double minPurchase;

    public DiscountCode(String code, String type, double value, double minPurchase) {
        this.code = code;
        this.type = type;
        this.value = value;
        this.minPurchase = minPurchase;
    }

    public String getCode() { return code; }
    public double getMinPurchase() { return minPurchase; }

    public double calculateDiscount(double subtotal) {
        if (subtotal < minPurchase) return 0.0;
        return type.equals("PERCENTAGE") ? subtotal * (value / 100.0) : Math.min(value, subtotal);
    }
}

class ShoppingCart {
    private final Map<Integer, CartItem> items;
    private DiscountCode appliedDiscount;

    public ShoppingCart() {
        this.items = new HashMap<>();
        this.appliedDiscount = null;
    }

    public void addItem(Product product, int quantity) {
        CartItem existing = items.get(product.getId());
        if (existing != null) {
            existing.setQuantity(existing.getQuantity() + quantity);
        } else {
            items.put(product.getId(), new CartItem(product, quantity));
        }
        System.out.println("Added " + quantity + "x " + product.getName());
    }

    public void removeItem(int productId) {
        items.remove(productId);
        System.out.println("Removed item");
    }

    public void updateQuantity(int productId, int newQty) {
        CartItem item = items.get(productId);
        if (item != null) {
            item.setQuantity(newQty);
            System.out.println("Updated quantity to " + newQty);
        }
    }

    public void applyDiscount(DiscountCode discount) {
        this.appliedDiscount = discount;
        System.out.println("Applied discount: " + discount.getCode());
    }

    public double getSubtotal() {
        return items.values().stream().mapToDouble(CartItem::getSubtotal).sum();
    }

    public double getDiscount() {
        return appliedDiscount == null ? 0.0 : appliedDiscount.calculateDiscount(getSubtotal());
    }

    public double getTotal() {
        return getSubtotal() - getDiscount();
    }

    public void displayCart() {
        if (items.isEmpty()) {
            System.out.println("Cart is empty");
            return;
        }
        System.out.println("\n=== SHOPPING CART ===");
        items.values().forEach(System.out::println);
        System.out.printf("Subtotal: $%.2f\n", getSubtotal());
        if (appliedDiscount != null) {
            System.out.printf("Discount: -$%.2f\n", getDiscount());
        }
        System.out.printf("TOTAL: $%.2f\n\n", getTotal());
    }

    public void checkout() {
        System.out.println("\n=== ORDER CONFIRMED ===");
        displayCart();
        System.out.println("Thank you for your purchase!");
        items.clear();
    }
}

public class Main {
    public static void main(String[] args) {
        System.out.println("=== E-COMMERCE SHOPPING CART ===\n");

        Product laptop = new Product(1, "Laptop Pro", 1299.99, "Electronics");
        Product mouse = new Product(2, "Wireless Mouse", 29.99, "Accessories");
        Product keyboard = new Product(3, "Mechanical Keyboard", 89.99, "Accessories");

        ShoppingCart cart = new ShoppingCart();

        cart.addItem(laptop, 1);
        cart.addItem(mouse, 2);
        cart.addItem(keyboard, 1);
        cart.displayCart();

        cart.updateQuantity(2, 3);
        cart.displayCart();

        DiscountCode discount = new DiscountCode("SAVE10", "PERCENTAGE", 10, 100);
        cart.applyDiscount(discount);
        cart.displayCart();

        cart.checkout();
    }
}

