import java.util.*;
import java.time.*;
import java.time.format.*;

class WeatherData {
    private final String city;
    private final double temperature;
    private final String conditions;
    private final int humidity;
    private final LocalDateTime timestamp;

    public WeatherData(String city, double temp, String cond, int humid) {
        this.city = city;
        this.temperature = temp;
        this.conditions = cond;
        this.humidity = humid;
        this.timestamp = LocalDateTime.now();
    }

    public String getCity() { return city; }

    @Override
    public String toString() {
        return String.format("City: %s | Temp: %.1fF | %s | Humidity: %d%%",
            city, temperature, conditions, humidity);
    }
}

class CacheEntry<T> {
    private final T value;
    private final LocalDateTime expiresAt;

    public CacheEntry(T value, int ttlMinutes) {
        this.value = value;
        this.expiresAt = LocalDateTime.now().plusMinutes(ttlMinutes);
    }

    public T getValue() { return value; }

    public boolean isExpired() {
        return LocalDateTime.now().isAfter(expiresAt);
    }
}

class CacheManager<K, V> {
    private final Map<K, CacheEntry<V>> cache = new HashMap<>();
    private final int ttlMinutes;

    public CacheManager(int ttlMinutes) {
        this.ttlMinutes = ttlMinutes;
    }

    public void put(K key, V value) {
        cache.put(key, new CacheEntry<>(value, ttlMinutes));
        System.out.println("[CACHE] Stored: " + key);
    }

    public V get(K key) {
        CacheEntry<V> entry = cache.get(key);
        if (entry == null) {
            System.out.println("[CACHE MISS] " + key);
            return null;
        }
        if (entry.isExpired()) {
            cache.remove(key);
            System.out.println("[CACHE EXPIRED] " + key);
            return null;
        }
        System.out.println("[CACHE HIT] " + key);
        return entry.getValue();
    }

    public void clear() {
        cache.clear();
        System.out.println("[CACHE] Cleared");
    }
}

interface WeatherAPI {
    WeatherData getWeather(String city);
}

class MockWeatherAPI implements WeatherAPI {
    private final Random random = new Random();
    private final Map<String, String> cities = Map.of(
        "New York", "Partly Cloudy",
        "Los Angeles", "Sunny",
        "Chicago", "Cloudy"
    );

    @Override
    public WeatherData getWeather(String city) {
        System.out.println("[API CALL] Fetching weather for " + city);
        try { Thread.sleep(100); } catch (InterruptedException e) {}

        if (!cities.containsKey(city)) {
            System.out.println("ERROR: City not found");
            return null;
        }

        return new WeatherData(
            city,
            60 + random.nextDouble() * 40,
            cities.get(city),
            40 + random.nextInt(40)
        );
    }
}

class WeatherDashboard {
    private final WeatherAPI api;
    private final CacheManager<String, WeatherData> cache;

    public WeatherDashboard(WeatherAPI api, int cacheTTL) {
        this.api = api;
        this.cache = new CacheManager<>(cacheTTL);
    }

    public WeatherData getWeather(String city) {
        WeatherData cached = cache.get(city);
        if (cached != null) return cached;

        WeatherData fresh = api.getWeather(city);
        if (fresh != null) {
            cache.put(city, fresh);
        }
        return fresh;
    }

    public void display(String city) {
        System.out.println("\n=== WEATHER DASHBOARD ===");
        WeatherData weather = getWeather(city);
        if (weather != null) {
            System.out.println(weather);
        } else {
            System.out.println("No data available for " + city);
        }
        System.out.println();
    }
}

public class Main {
    public static void main(String[] args) {
        System.out.println("=== WEATHER API DEMO ===\n");

        WeatherAPI api = new MockWeatherAPI();
        WeatherDashboard dashboard = new WeatherDashboard(api, 5);

        System.out.println("--- First Request (API) ---");
        dashboard.display("New York");

        System.out.println("--- Second Request (Cache) ---");
        dashboard.display("New York");

        System.out.println("--- Different City ---");
        dashboard.display("Los Angeles");

        System.out.println("--- Invalid City ---");
        dashboard.display("Atlantis");
    }
}

