import java.util.*;
import java.time.*;
import java.time.format.*;

class ShortenedURL {
    private final String originalURL;
    private final String shortCode;
    private final LocalDateTime createdAt;
    private LocalDateTime expiresAt;
    private int clickCount;
    private LocalDateTime lastAccessed;

    public ShortenedURL(String originalURL, String shortCode, int expiryDays) {
        this.originalURL = originalURL;
        this.shortCode = shortCode;
        this.createdAt = LocalDateTime.now();
        this.expiresAt = expiryDays > 0 ? createdAt.plusDays(expiryDays) : null;
        this.clickCount = 0;
        this.lastAccessed = null;
    }

    public String getOriginalURL() { return originalURL; }
    public String getShortCode() { return shortCode; }
    public int getClickCount() { return clickCount; }

    public boolean isExpired() {
        return expiresAt != null && LocalDateTime.now().isAfter(expiresAt);
    }

    public void recordClick() {
        clickCount++;
        lastAccessed = LocalDateTime.now();
    }

    public String getStats() {
        DateTimeFormatter formatter = DateTimeFormatter.ofPattern("MMM dd, yyyy HH:mm");
        StringBuilder sb = new StringBuilder();
        sb.append(String.format("Short Code: %s\n", shortCode));
        sb.append(String.format("Original: %s\n", originalURL));
        sb.append(String.format("Clicks: %d\n", clickCount));
        sb.append(String.format("Created: %s\n", createdAt.format(formatter)));
        if (expiresAt != null) {
            sb.append(String.format("Expires: %s\n", expiresAt.format(formatter)));
        }
        if (lastAccessed != null) {
            sb.append(String.format("Last Accessed: %s\n", lastAccessed.format(formatter)));
        }
        return sb.toString();
    }
}

class URLShortener {
    private final Map<String, ShortenedURL> shortCodeMap;
    private final Map<String, String> urlToCodeMap;
    private final Random random;
    private static final String CHARS = "abcdefghijklmnopqrstuvwxyzABCDEFGHIJKLMNOPQRSTUVWXYZ0123456789";
    private static final int CODE_LENGTH = 6;

    public URLShortener() {
        this.shortCodeMap = new HashMap<>();
        this.urlToCodeMap = new HashMap<>();
        this.random = new Random();
    }

    private String generateShortCode() {
        StringBuilder code = new StringBuilder();
        for (int i = 0; i < CODE_LENGTH; i++) {
            code.append(CHARS.charAt(random.nextInt(CHARS.length())));
        }
        return code.toString();
    }

    public String shorten(String url) {
        return shorten(url, null, 0);
    }

    public String shorten(String url, String customCode, int expiryDays) {
        // Check if URL already shortened
        if (urlToCodeMap.containsKey(url)) {
            String existingCode = urlToCodeMap.get(url);
            System.out.println("URL already shortened: " + existingCode);
            return existingCode;
        }

        // Use custom code or generate new one
        String shortCode;
        if (customCode != null && !customCode.isEmpty()) {
            if (shortCodeMap.containsKey(customCode)) {
                System.out.println("ERROR: Custom code already in use");
                return null;
            }
            shortCode = customCode;
        } else {
            // Generate unique code
            int attempts = 0;
            do {
                shortCode = generateShortCode();
                attempts++;
                if (attempts > 10) {
                    System.out.println("ERROR: Too many collisions");
                    return null;
                }
            } while (shortCodeMap.containsKey(shortCode));
        }

        ShortenedURL shortened = new ShortenedURL(url, shortCode, expiryDays);
        shortCodeMap.put(shortCode, shortened);
        urlToCodeMap.put(url, shortCode);

        System.out.println("Created short URL: " + shortCode);
        return shortCode;
    }

    public String expand(String shortCode) {
        ShortenedURL url = shortCodeMap.get(shortCode);

        if (url == null) {
            System.out.println("ERROR: Short code not found");
            return null;
        }

        if (url.isExpired()) {
            System.out.println("ERROR: Short URL has expired");
            shortCodeMap.remove(shortCode);
            urlToCodeMap.remove(url.getOriginalURL());
            return null;
        }

        url.recordClick();
        System.out.println("Redirecting to: " + url.getOriginalURL());
        return url.getOriginalURL();
    }

    public void showStats(String shortCode) {
        ShortenedURL url = shortCodeMap.get(shortCode);
        if (url == null) {
            System.out.println("ERROR: Short code not found");
            return;
        }

        System.out.println("\n=== URL STATISTICS ===");
        System.out.println(url.getStats());
    }

    public void listAll() {
        System.out.println("\n=== ALL SHORT URLS ===");
        if (shortCodeMap.isEmpty()) {
            System.out.println("No URLs created yet");
            return;
        }

        shortCodeMap.values().forEach(url -> {
            System.out.printf("%s -> %s (clicks: %d)%s\n",
                url.getShortCode(),
                url.getOriginalURL(),
                url.getClickCount(),
                url.isExpired() ? " [EXPIRED]" : "");
        });
        System.out.println();
    }

    public int getTotalClicks() {
        return shortCodeMap.values().stream()
            .mapToInt(ShortenedURL::getClickCount)
            .sum();
    }
}

public class Main {
    public static void main(String[] args) {
        System.out.println("=== URL SHORTENER SERVICE ===\n");

        URLShortener shortener = new URLShortener();

