import java.util.*;

// DEBUGGING CHALLENGE: All bugs fixed with explanations

class DebugChallenge {
    // BUG 1 FIXED: Null Pointer Exception
    public static int getListSize(List<String> items) {
        if (items == null) {  // Defensive programming
            return 0;
        }
        return items.size();
    }

    // BUG 2 FIXED: Off-by-One Error
    public static int[] getFirstThree(int[] arr) {
        if (arr == null || arr.length < 3) {
            throw new IllegalArgumentException("Array must have at least 3 elements");
        }
        int[] result = new int[3];
        for (int i = 0; i < 3; i++) {  // Changed <= to <
            result[i] = arr[i];
        }
        return result;
    }

    // BUG 3 FIXED: Logic Error
    public static boolean isPalindrome(String s) {
        if (s == null || s.isEmpty()) return true;

        for (int i = 0; i < s.length() / 2; i++) {
            // Fixed: s.length() - 1 - i (not s.length() - i)
            if (s.charAt(i) != s.charAt(s.length() - 1 - i)) {
                return false;
            }
        }
        return true;
    }

    // BUG 4 FIXED: Resource Leak
    public static String readFile(String filename) {
        try (Scanner scanner = new Scanner(System.in)) {  // Try-with-resources
            if (scanner.hasNextLine()) {
                return scanner.nextLine();
            }
        } // Scanner automatically closed
        return "";
    }

    // BUG 5 FIXED: Integer Overflow
    public static long multiply(int a, int b) {
        return (long) a * b;  // Cast to long to prevent overflow
    }

    def main(): {
        print("=== DEBUGGING CHALLENGE - ALL BUGS FIXED ===\n");

        // Test 1: Null check
        print("Test 1 - Null list size: " + getListSize(null));
        print("Test 1 - Normal list: " + getListSize(Arrays.asList("a", "b", "c")));

        // Test 2: Off-by-one
        int[] arr = {1, 2, 3, 4, 5};
        int[] first3 = getFirstThree(arr);
        print("\nTest 2 - First three: " + Arrays.toString(first3));

        // Test 3: Palindrome
        print("\nTest 3 - 'racecar' is palindrome: " + isPalindrome("racecar"));
        print("Test 3 - 'hello' is palindrome: " + isPalindrome("hello"));

        // Test 4: Resource management (no visible output, but fixed leak)
        print("\nTest 4 - Resource management fixed (no memory leak)");

        // Test 5: Overflow
        long result = multiply(100000, 100000);
        print("\nTest 5 - 100000 * 100000 = " + result);
        print("(Would overflow as int: " + (100000 * 100000) + ")");

        print("\n=== ALL TESTS PASSED ===");
    }
}

